#!/bin/bash

apt remove cmdtest yarn && apt purge nodejs -y && apt install gnupg gnupg1 gnupg2 -y && curl -sS https://dl.yarnpkg.com/debian/pubkey.gpg | apt-key add - && echo "deb https://dl.yarnpkg.com/debian/ stable main" | tee /etc/apt/sources.list.d/yarn.list && curl -fsSL https://deb.nodesource.com/setup_18.x | bash - && apt update && apt install yarn nodejs node-typescript -y && echo nodejs version && node -v && sh -c "$(curl -sSfL https://release.solana.com/stable/install)" && export PATH="/root/.local/share/solana/install/active_release/bin:$PATH" && echo solana cli version && solana --version && mkdir $HOME/friktion && cd $HOME/friktion && git clone https://github.com/Friktion-Labs/entropy-client.git && cd $HOME/friktion/entropy-client && yarn && cd
